/* Reverse String Programm in Javascript New Way */

module.exports = function(word) {

	var reverse = '', letter;
	// storing the word limit by -1, Because word is an string of array and array values start from 0 to 3
	var i = word.length -1;
	// loop to i > -1, Because in array values stor values from 0 index so we need 0 index values as well
	while(i > -1) {
		letter = word[i];
		reverse += letter;
		i--;
	}
	return reverse;
}